DEFAULT_COLORTABLEID=4

// Default the colorTableId
msg.colorTableId= msg.payload.colorTableId ? msg.payload.colorTableId: DEFAULT_COLORTABLEID

msg.payload = `sld?type=raster&min=${msg.payload.min}&max=${msg.payload.max}&colorTableId=${msg.colorTableId}&no_data=-1`

return msg;

/* generate for rectangle selection query */
const pos=msg.payload.pos

msg.payload["name"]=`VitXplore_Rect[${pos.rSouth};${pos.rWest};${pos.rNorth};${pos.rEast}]_[${msg.payload.startDay}/${msg.payload.endDay}]`
msg.payload["spatial"]={ "type": "square", "coordinates": [ pos.rSouth, pos.rWest, pos.rNorth, pos.rEast]}

return msg

/* generate for rectangle selection query */
const pos=msg.payload.pos

msg.payload["name"]=`VitXplore_${msg.payload.aoi.name}_[${pos.lat};${pos.lng}]_[${msg.payload.startDay}/${msg.payload.endDay}]`
msg.payload["spatial"]= { "type": "poly", "aoi": msg.payload.aoi.id}

return msg

/* Setup payload query parameters */
payload={"name":msg.payload.name, 
         "spatial":msg.payload.spatial,
         "layers":[{"alias": "Overall Scoring", "expression": msg.payload.udf }],
         "temporal":{ "intervals": [{ "start": `${msg.payload.startDay}T00:00:00Z`, "end": `${msg.payload.endDay}T00:00:00Z` }]}
        }

const DEPTHS = ["0to5cm","5to15cm","15to30cm","30to60cm","60to100cm", "100to200cm"]

for (const i in msg.payload.layers) {
    var layerId = msg.payload.layers[i].id
    var layer=msg.payload.layerInfo[layerId]

    if ((layer.measurement_interval == "0 years 0 mons 0 days 0 hours 0 mins 0.00 secs") ||
        (layer.measurement_interval == "0 years 0 mons 0 days 0 hours 0 mins 0.0 secs")) {
        /* handle non-temporal layers */

        if (layer.dimensions_description) {
            /* Handle layers that have a depth */
            for (var j = 0; j < DEPTHS.length; j++) {
                payload.layers.push({ "type": "raster",
                                      "id":layerId,
                                      "alias": `depth${DEPTHS[j]}_${layerId}`,
                                      "aggregation": "Mean",
                                      "temporal" : {"intervals" : [{"snapshot" : "2020-09-01T00:00:01Z"}]},
                                      "dimensions" : [{"name" : "quantile", "value" : "mean"}, {"name" : "depth", "value" : DEPTHS[j] }] })
            }
        } else {
            /* Other non-depth non-temporal layers */
            payload.layers.push({ "type": "raster",
                                  "id": layerId,
                                  "alias": `Mean_${layerId}`,
                                  "aggregation": "Mean",
                                  "temporal" : {"intervals" : [{"snapshot" : "2020-09-01T00:00:01Z"}]} })
        }
    } else {
        /* handle regular temporal layers, compute mean value */
        payload.layers.push({ "type": "raster",
                              "id": layerId,
                              "alias": `Mean_${layerId}`,
                              "aggregation": "Mean"})
    }
}

msg.payload=payload
return msg;

var payload = {
    "layers": [],
    "spatial": { "type": "point", "coordinates": [ msg.payload.pos.lat, msg.payload.pos.lng]},
    "temporal": { "intervals": [{ "start": msg.payload.startDay + "T00:00:00Z", "end": msg.payload.endDay + "T00:00:00Z" }] }
};


var depths = ["0to5cm","5to15cm","15to30cm","30to60cm","60to100cm", "100to200cm"]

for (const layerId in msg.payload.layers) {
    if ((msg.payload.layers[layerId].measurement_interval == "0 years 0 mons 0 days 0 hours 0 mins 0.0 secs") || msg.payload.layers[layerId].measurement_interval == "0 years 0 mons 0 days 0 hours 0 mins 0.00 secs") {
        if (msg.payload.layers[layerId].dimensions_description) {
            for (var j = 0; j < depths.length; j++) {
                payload.layers.push({ "type": "raster", "id": layerId, "aggregation": "None", "temporal" : {"intervals" : [{"snapshot" : "2020-09-01T00:00:01Z"}]}, "dimensions" : [{"name" : "quantile", "value" : "mean"}, {"name" : "depth", "value" : depths[j] }] })
            }
        } else {
            payload.layers.push({ "type": "raster", "id": layerId, "aggregation": "None", "temporal" : {"intervals" : [{"snapshot" : "2020-09-01T00:00:01Z"}]} })
        }
    } else {
        payload.layers.push({ "type": "raster", "id": layerId, "aggregation": "Mean"})
        payload.layers.push({ "type": "raster", "id": layerId, "aggregation": "Max"})
        payload.layers.push({ "type": "raster", "id": layerId, "aggregation": "Min"})
    }
}

msg.payload = payload;
return msg;
